import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AdminPage.css'; // new styles

export default function AdminPage() {
  const API = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();
  const [surveys, setSurveys] = useState([]);
  const [cur, setCur] = useState(null);
  const [form, setForm] = useState({ title: '', description: '' });
  const [questions, setQuestions] = useState([]);
  const [newQ, setNewQ] = useState({ text: '', type: 'text' });
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`${API}/usf/me`, { withCredentials: true })
      .then(r => {
        if (r.data.user !== 'ADMIN') navigate('/login');
        else loadSurveys();
      })
      .catch(() => navigate('/login'));
  }, []);

  const loadSurveys = () => {
    axios.get(`${API}/usf/surveys`, { withCredentials: true })
      .then(r => setSurveys(r.data.surveys))
      .catch(() => setError('Error loading surveys'));
  };

  const selectSurvey = (id) => {
    setCur(id);
    axios.get(`${API}/usf/surveys/${id}`, { withCredentials: true })
      .then(r => {
        setForm({ title: r.data.title, description: r.data.description });
        setQuestions(r.data.questions);
      })
      .catch(() => setError('Error loading survey'));
  };

  const saveSurvey = () => {
    axios.put(`${API}/usf/surveys/${cur}`, form, { withCredentials: true })
      .then(loadSurveys)
      .catch(() => setError('Error saving survey'));
  };

  const addQuestion = () => {
    if (!newQ.text.trim()) return;
    axios.post(`${API}/usf/surveys/${cur}/questions`, newQ, { withCredentials: true })
      .then(() => {
        setNewQ({ text: '', type: 'text' });
        selectSurvey(cur);
      })
      .catch(() => setError('Error adding question'));
  };

  const updateQuestion = (q) => {
    axios.put(`${API}/usf/surveys/${cur}/questions/${q.id}`, q, { withCredentials: true })
      .then(() => selectSurvey(cur))
      .catch(() => setError('Error updating question'));
  };

  const deleteQuestion = id => {
    axios.delete(`${API}/usf/surveys/${cur}/questions/${id}`, { withCredentials: true })
      .then(() => selectSurvey(cur))
      .catch(() => setError('Error deleting question'));
  };

  return (
    <div className="admin-form-container">
      <header className="admin-header">
        <h1>Survey Admin</h1>
        {error && <div className="admin-error">{error}</div>}
        {!cur && (
          <>
            <div className="admin-select-survey">
              <label>Open Survey:</label>
              <select onChange={e => selectSurvey(e.target.value)} defaultValue="">
                <option value="" disabled>Select one‚Ä¶</option>
                {surveys.map(s => <option key={s.id} value={s.id}>{s.title}</option>)}
              </select>
            </div>
            <button className="btn-create-survey" onClick={() => {
              const title = prompt("New survey title:");
              if (!title) return;
              axios.post(`${API}/usf/surveys`, { title, description: '' }, { withCredentials: true })
                .then(loadSurveys);
            }}>+ Create Survey</button>
          </>
        )}

        {cur && (
          <>
            <div className="survey-meta-editor">
              <input
                className="survey-title-input"
                value={form.title}
                placeholder="Survey title"
                onChange={e => setForm({ ...form, title: e.target.value })}
              />
              <textarea
                className="survey-desc-input"
                value={form.description}
                placeholder="Form description"
                onChange={e => setForm({ ...form, description: e.target.value })}
              />
              <button className="btn-save-survey" onClick={saveSurvey}>Save Form</button>
              <button
                className="btn-back"
                onClick={() => setCur(null)}
              >‚Üê Back</button>
            </div>

            <div className="questions-editor">
              {questions.map(q => (
                <div key={q.id} className="question-card">
                  <input
                    className="question-text"
                    value={q.text}
                    onChange={e => updateQuestion({ ...q, text: e.target.value })}
                  />
                  <select
                    value={q.type}
                    onChange={e => updateQuestion({ ...q, type: e.target.value })}
                  >
                    <option value="text">Short answer</option>
                    <option value="scale">Scale (1‚Äì5)</option>
                    <option value="multiple">Multiple choice</option>
                  </select>
                  <button className="btn-delete-question" onClick={() => deleteQuestion(q.id)}>üóë</button>
                </div>
              ))}

              <div className="question-card new">
                <input
                  className="question-text"
                  placeholder="Add your question..."
                  value={newQ.text}
                  onChange={e => setNewQ({ ...newQ, text: e.target.value })}
                />
                <select
                  value={newQ.type}
                  onChange={e => setNewQ({ ...newQ, type: e.target.value })}
                >
                  <option value="text">Short answer</option>
                  <option value="scale">Scale (1‚Äì5)</option>
                  <option value="multiple">Multiple choice</option>
                </select>
                <button className="btn-add-question" onClick={addQuestion}>+</button>
              </div>
            </div>
          </>
        )}
      </header>
    </div>
  );
}
